mapper.update:
update emp
username = #{username}, password = #{password}, name = #{name}, gender = #{gender}, phone = #{phone}, job = #{job}, salary = #{salary}, dept_id = #{deptId}, entry_date = #{entryDate}, image = #{image}, update_time = #{updateTime},
where id = #{id}

mapper.select:
#  resultType指定要封装成哪个类型的对象
        select e.*, d.name deptName from emp e left join dept d on e.dept_id = d.id


                e.name like concat('%', #{name}, '%')


                and e.gender = #{gender}


                and e.entry_date between #{begin} and #{end}


        order by e.update_time desc

select
    if(gender = 1, '男性员工', '女性员工') name,
    count(*) value
    from emp group by emp.gender;
